CREATE TABLE Companies (
    company_id   NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    company_name VARCHAR2(255) NOT NULL UNIQUE
);

CREATE TABLE Drugs (
    drug_id       NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    drug_name     VARCHAR2(255) NOT NULL,
    product_name  VARCHAR2(255),
    drug_category VARCHAR2(255),
    company_id    NUMBER,
    CONSTRAINT fk_company FOREIGN KEY (company_id) REFERENCES Companies(company_id)
);

CREATE TABLE Side_Effects (
    side_effect_id   NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    drug_id          NUMBER,
    side_effect_name VARCHAR2(255) NOT NULL,
    CONSTRAINT fk_drug_side_effect FOREIGN KEY (drug_id) REFERENCES Drugs(drug_id)
);

CREATE TABLE Interactions (
    interaction_id  NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    drug_id         NUMBER,
    interacts_with  VARCHAR2(255) NOT NULL,
    CONSTRAINT fk_drug_interaction FOREIGN KEY (drug_id) REFERENCES Drugs(drug_id)
);

CREATE TABLE Diseases (
    disease_id       NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    disease_name     VARCHAR2(255) NOT NULL UNIQUE,
    disease_category VARCHAR2(255)
);

CREATE TABLE Drug_Diseases (
    drug_id    NUMBER,
    disease_id NUMBER,
    PRIMARY KEY (drug_id, disease_id),
    CONSTRAINT fk_drug_dd FOREIGN KEY (drug_id) REFERENCES Drugs(drug_id),
    CONSTRAINT fk_disease_dd FOREIGN KEY (disease_id) REFERENCES Diseases(disease_id)
);

CREATE TABLE Clinical_Trials (
    trial_id          NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    trial_title       VARCHAR2(512) NOT NULL,
    start_date        DATE,
    completion_date   DATE,
    participants      NUMBER,
    status            VARCHAR2(100),
    institution       VARCHAR2(255),
    address           VARCHAR2(255),
    main_researcher   VARCHAR2(255)
);

CREATE TABLE Trial_Conditions (
    condition_id   NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    trial_id       NUMBER,
    condition_name VARCHAR2(255) NOT NULL,
    CONSTRAINT fk_trial_condition FOREIGN KEY (trial_id) REFERENCES Clinical_Trials(trial_id)
);

CREATE TABLE Drug_Trials (
    drug_id  NUMBER,
    trial_id NUMBER,
    PRIMARY KEY (drug_id, trial_id),
    CONSTRAINT fk_drug_dt FOREIGN KEY (drug_id) REFERENCES Drugs(drug_id),
    CONSTRAINT fk_trial_dt FOREIGN KEY (trial_id) REFERENCES Clinical_Trials(trial_id)
);









